import React from "react";
import "./Quote.css";

const Quote = () => {
  return (
    <div className="quote">
      <div className="box">
        <h3>
          ‘Sunlight fell upon the wall; the wall received a borrowed splendour.’
        </h3>
        <p>Jalāl ad-Dīn Muhammad Rūmī</p>
      </div>
    </div>
  );
};

export default Quote;
